import React, { useState, useEffect } from 'react';
import './ViewPartner.css'; // Don't forget to create this CSS file

function ViewPartner({ partner, onClose, onUpdatePartner, onDeletePartner }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedPartner, setEditedPartner] = useState({});
  const [iconFile, setIconFile] = useState(null);

  useEffect(() => {
    if (partner) {
      setEditedPartner({
        id: partner.id,
        name: partner.name,
        // Assuming detection settings are an array of strings like ['PERSON', 'LOCATION']
        detection: partner.detection || [],
        key: partner.key || '', // Assuming 'key' is part of secret info
        password: partner.password || '' // Assuming 'password' is part of secret info
      });
      setIconFile(null); // Reset icon file when partner changes
    }
  }, [partner]);

  if (!partner) {
    return (
      <div className="modal-overlay">
        <div className="view-partner-modal-content">
          <p>No partner selected for viewing.</p>
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    );
  }

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setEditedPartner(prev => {
        const newDetection = checked
          ? [...prev.detection, value]
          : prev.detection.filter(d => d !== value);
        return { ...prev, detection: newDetection };
      });
    } else {
      setEditedPartner(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleIconChange = (e) => {
    setIconFile(e.target.files[0]);
  };

  const handleSubmitUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('partnerId', editedPartner.id); // Assuming backend needs the ID
    formData.append('name', editedPartner.name);
    formData.append('detection', JSON.stringify(editedPartner.detection)); // Send as JSON string
    formData.append('key', editedPartner.key);
    formData.append('password', editedPartner.password);
    if (iconFile) {
      formData.append('icon', iconFile);
    }

    // Call the onUpdatePartner prop passed from App.jsx
    await onUpdatePartner(editedPartner.id, formData);
    setIsEditing(false); // Exit editing mode after update
  };

  const handleDelete = async () => {
    if (window.confirm(`Are you sure you want to delete partner "${partner.name}"? This action cannot be undone.`)) {
      await onDeletePartner(partner.id); // Call the onDeletePartner prop
      onClose(); // Close the modal after deletion
    }
  };

  return (
    <div className="modal-overlay">
      <div className="view-partner-modal-content">
        <div className="modal-header">
          <h2>{isEditing ? `Edit Partner: ${partner.name}` : `Partner Details: ${partner.name}`}</h2>
          <button className="close-button" onClick={onClose}>&times;</button>
        </div>
        <div className="modal-body">
          <div className="partner-icon-display">
            {partner.icon ? (
              <img src={`${partner.icon}`} alt={`${partner.name} icon`} className="partner-view-icon" />
            ) : (
              <div className="partner-view-icon-placeholder">No Icon</div>
            )}
          </div>
          {!isEditing ? (
            // Display Mode
            <div className="partner-details-display">
              <p><strong>Name:</strong> {partner.name}</p>
              <p><strong>Detection Settings:</strong> {partner.detection && partner.detection.length > 0 ? partner.detection.join(', ') : 'None selected'}</p>
              {/* Note: Do NOT display sensitive info like key/password here */}
            </div>
          ) : (
            // Edit Mode Form
            <form onSubmit={handleSubmitUpdate} className="edit-partner-form">
              <div className="form-group">
                <label>Partner Name:</label>
                <input
                  type="text"
                  name="name"
                  value={editedPartner.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Partner Icon (optional):</label>
                <input
                  type="file"
                  name="icon"
                  accept="image/*"
                  onChange={handleIconChange}
                />
              </div>
              <div className="form-group">
                <label>Detection Settings:</label>
                <div className="checkbox-group">
                  {['PERSON', 'US_PASSPORT', 'LOCATION', 'PHONE_NUMBER', 'IC_NUMBER', 'EMAIL_ADDRESS', 'US_BANK_NUMBER', 'CREDIT_CARD'].map(detectionType => (
                    <label key={detectionType} className="checkbox-label">
                      <input
                        type="checkbox"
                        name="detection"
                        value={detectionType}
                        checked={editedPartner.detection.includes(detectionType)}
                        onChange={handleInputChange}
                      />
                      {detectionType.replace(/_/g, ' ')}
                    </label>
                  ))}
                </div>
              </div>
              {/* Add secret info fields if they are editable, but be mindful of security */}
              <div className="form-group">
                <label>API Key:</label>
                <input
                  type="text"
                  name="key"
                  value={editedPartner.key}
                  onChange={handleInputChange}
                  placeholder="Enter API Key"
                />
              </div>
              <div className="form-group">
                <label>API Password:</label>
                <input
                  type="password"
                  name="password"
                  value={editedPartner.password}
                  onChange={handleInputChange}
                  placeholder="Enter API Password"
                />
              </div>
              <div className="form-actions">
                <button type="submit" className="save-button">Save Changes</button>
                <button type="button" className="cancel-button" onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            </form>
          )}
        </div>
        <div className="modal-footer">
          {!isEditing && (
            <>
              <button className="edit-button" onClick={() => setIsEditing(true)}>Edit Partner</button>
              <button className="delete-button" onClick={handleDelete}>Delete Partner</button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default ViewPartner;
