export const piiCategories = {
    'Individuals': ['PERSON', 'IC_NUMBER', 'PHONE_NUMBER', 'EMAIL_ADDRESS', 'US_PASSPORT'],
    'Organizations': ['ORGANIZATION'],
    'Financial': ['CREDIT_CARD', 'US_BANK_NUMBER'], // Assuming US_BANK_NUMBER is for IBAN-like numbers
    'Location': ['LOCATION'],
    'Dates': ['DATE'],
};

// Helper function to get the category for a given PII type
export const getCategoryForPiiType = (piiType) => {
    for (const category in piiCategories) {
        if (piiCategories[category].includes(piiType)) {
            return category;
        }
    }
    return 'Other'; 
};
